'use strict';

import React from 'react';

export default function (props) {
    var profileUrl = props.profileUrl ? props.profileUrl : 'https://github.com/' + props.userName;
    return React.createElement(
        'a',
        { href: profileUrl, style: props.styles.githubProfile },
        React.createElement(
            'div',
            { style: props.styles.githubFullname },
            props.fullName
        ),
        React.createElement(
            'div',
            { style: props.styles.githubUsername },
            props.userName
        )
    );
}